from stack import Stack


def is_match(s: str) -> bool:
    """括号匹配"""
    dt = {"}": "{", "]": "[", ")": "("}
    stack = Stack(len(s))
    for val in s:
        if not stack.is_empty() and val in dt:
            if stack.get_top() != dt.get(val):
                return False
            else:
                stack.pop()
        else:
            stack.push(val)
        # if val in dt.values():
        #     stack.push(val)
        # elif val in dt.keys():
        #     if stack.is_empty():
        #         return False
        #     res = stack.pop()
        #     if res != dt.get(val):
        #         return False
        # else:
        #     raise ValueError("输入不合法...")
    return stack.is_empty()


