def is_match(s: str) -> bool:
    """括号匹配"""
    dt = {"}": "{", "]": "[", ")": "("}
    stack = []
    for val in s:
        if stack and val in dt:
            if stack[-1] == dt[val]:
                stack.pop()
            else:
                return False
        else:
            stack.append(val)
    return not stack


